CREATE TABLE `cancionero` (
  `id` int(3) NOT NULL,
  `nombre` longtext COLLATE utf8_unicode_ci NOT NULL,
  `cancion` longtext COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Volcado de datos para la tabla `cancionero`
--

INSERT INTO `cancionero` (`id`, `nombre`, `cancion`) VALUES
(1, 'señor,ante ti', 'Señor ante Ti, hoy vengo a dejar\r\nmis proyectos mi vida, mis sueños, mi libertad,\r\nSeñor desde hoy, mi felicidad\r\nserá amarte y servirte buscando hacer Tu voluntad.\r\nRecibe señor en tus manos la ofrenda que hoy vengo a traer ante Ti,\r\nreviste con tu fortaleza mi débil pobreza que hoy te dice Si,\r\ny nunca permitas que crea que serte fiel solo depende de mi,\r\nhoy traigo en vasijas de barro el tesoro que sin merecer recibí.\r\nSeñor aquí estoy, Tu sabes como soy\r\ncon Tu gracia transforma mi alma limpia mi corazón,\r\nSeñor haz que en mi, siempre crezca esta sed,\r\nde querer que en tus aguas profundas,\r\npueda un día beber.\r\nRecibe señor en tus manos...\r\nSeñor quiero estar en la necesidad\r\ndel hermano que sufre violencia sin pan ni libertad\r\nSeñor poco soy para poder luchar,\r\npero se que mi pequeña ofrenda tu la transformaras.\r\nRecibe Señor en tus manos...\'),\r\n(\'Dios esta quí\',\'Dios esta aqui\r\ntan cierto como el aire que respiro,\r\ntan cierto como la mañana se levanta\r\ntan cierto como yo te hablo y me puedes oir.\r\nDios esta aqui\r\ntan cierto como el aire que respiro,\r\ntan cierto como la mañana se levanta\r\ntan cierto como yo te hablo y me puedes oir\r\nLo puedes sentir en este mismo instante\r\nlo puedes sentir muy cerca de tu corazon\r\nte puede ayudar en ese problema que tienes\r\nJesús esta aqui si tu quieres lo puedes sentir.\r\nJesús esta aqui si tu quieres lo puedes sentir.'),
(2, 'Esta es la casa del señor', 'ESTA ES LA CASA DEL SEÑOR\r\nLA MAS ALTA Y LA MAS BELLA QUE HAY|(BIS)\r\nDios la ha colocado sobre un monte como un\r\nfaro, como un faro. Dios la ha colocado sobre un\r\nmonte, para los hombres de buena voluntad.\r\nTiene sus entradas siempre abiertas, noche y\r\ndía, noche y día. Tiene sus entradas siempre\r\nabiertas, todos nosotros la tenemos que habitar\'),(\'Jesús esta entre nosostros\',\'JESÚS ESTÁ ENTRE NOSOTROS,\r\nÉL VIVE HOY\r\nY SU ESPÍRITU A TODOS DA.\r\nJESÚS, RAZÓN DE NUESTRA VIDA,\r\nES EL SEÑOR,\r\nNOS REÚNE EN PUEBLO DE AMOR.\r\nCambia nuestras vidas con tu fuerza.\r\nGuárdanos por siempre en tu presencia.\r\nTú eres verdad, Tú eres la paz.\r\nRompe las cadenas que nos atan.\r\nLlénanos de gracia en tu Palabra.\r\nGracias, Señor. Gracias, Salvador.\r\nNuestras existencias hoy te alaban.\r\nNuestros corazones te dan gracias.\r\nTú eres amor, Tú eres canción.'),
(3, 'Jesús esta en ti', '¡Es Jesús!\r\nque está en ti,\r\nque está en mí,\r\nque está en todos\r\nmis hermanos.\r\n\r\nQue está en ti,\r\nque está en mí,\r\nque está en todos\r\nmis hermanos.\r\n\r\nQuién hace crecer las flores,\r\nQuién hace descender el agua,\r\nQuién hace nuevo tu corazón,\r\ncada mañana.Quién hace amanecer el sol,\r\nQuién llena de estrellas el cielo,\r\nQuién en la noche más oscura,\r\nte da todo su consuelo.\r\n\r\n¡Es Jesús!\r\nque está en ti,\r\nque está en mí,\r\nque está en todos\r\nmis hermanos.\r\n\r\nQue está en ti,\r\nque está en mí,\r\nque está en todos\r\nmis hermanos.'),
(4, 'En su mesa hay amor', 'El señor nos a reunido junto a el\r\nEl señor nos a invitado a estar con el\r\nEn su mesa hay amor\r\nLa promesa del perdón\r\ny en el vino y pan su corazón \r\ncuando, señor, tu voz\r\nllega en silencio a mi\r\ny mis hermanos me hablan de ti\r\nse que a mi lado estas\r\nte sientas junto a mi\r\nacoges mi vida y mi oración\r\nEl señor nos a reunido junto a el\r\nEl señor nos a invitado a estar con el\r\nEn su mesa hay amor\r\nLa promesa del perdón\r\ny en el vino y pan su corazón\r\nel señor nos a reunido junto a el\'),\r\n(\'No tengo Miedo\',\'Cristo, tu cruz es respuesta real\r\nPara este mundo, para este tiempo\r\nQue huye en temores\r\nTú eres camino, eres verdad, eres la vida\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar\r\nAl verte herido reinando en la cruz\r\nDices mi nombre\r\nSuenan tambores al escucharte\r\nOigo las voces de aquellos hombres\r\nQue tienen hambre\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar\r\nSanta María, me acojo a tu amor\r\nPido tu fuego que arde de ruegos hoy por tus hijos\r\nVirgen María, Rosa del cielo, oye mi canto\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar'),
(5, 'yo celebraré', 'Yo celebraré, delante del Señor,\r\ncantaré un canto nuevo \r\nYo le alabaré, porque Él ha hecho\r\ngrandes cosas\r\nYo celebraré, delante del Señor,\r\ncantaré un canto nuevo \r\nYo le alabaré, porque Él ha hecho\r\ngrandes cosas\r\nYo celebraré, delante del Señor,\r\ncantaré un canto nuevo\r\n Yo le alabaré, porque Él ha hecho\r\ngrandes cosas\r\nYo celebraré, delante del Señor,\r\ncantaré un canto nuevo\r\nYo le alabaré, porque Él ha hecho\r\ngrandes cosas'),
(6, 'ABBA padre', 'Ante Ti venimos\r\npues 	Tú nos has llamado\r\ny nos atrae tu 	voz.\r\nComo un solo pueblo\r\ndanzando en tu presencia\r\nte damos el honor.\r\nSobre nosotros descienda el poder\r\nde tu Espíritu que nos hará clamar:\r\nAbba, Padre. Aabba, Padre.\r\nhoy tus hijos cantamos,\r\ntu amor celebramos\r\nclamando con una voz:\r\nAbba, Padre\r\nJuntos caminamos\r\nbuscando un a Palabra\r\nque nos habla de Ti.\r\nSolo tu presencia\r\nnos llena de esperanza,\r\nllenos para vivir.\r\nSobre nosotros descienda el poder\r\nde tu Espíritu que nos hará clamar:\r\nAbba, Padre. Aabba, Padre.\r\nhoy tus hijos cantamos,\r\ntu amor celebramos\r\nclamando con una voz:\r\nAbba, Padre'),
(7, 'Dos mil años de luz', 'Abrid las puertas de 	par en par\r\nque va a entrar el rey de la gloria.\r\nAbrid las puertas de par en par,\r\nque va a entrar el Hijo de Dios.\r\nPara celebrar dos mil a	ños de luz,\r\ndos mil años de misericordia.\r\nPara celebrar dos mil años de luz,\r\ndos mil años de liberación.\r\nCerrad los ojos y contemplad\r\nla presencia del cielo en nosotros.\r\nVivimos juntos en comunión:\r\nsantos, ángeles e hijos de Dios.\r\nPara celebrar dos mil a	ños de luz,\r\ndos mil años de miseri	cordia.\r\nPara celebrar dos mil años de luz,\r\ndos mil años de liberación.\r\nDanzad con júbilo ante el Señor\r\nque proclama un año de gracia.\r\nAlzad las voces para cantar\r\ny alabar ante el trono de Dios.\r\nPara celebrar dos mil a	ños de luz,\r\ndos mil años de miseri	cordia.\r\nPara celebrar dos mil años de luz,\r\ndos mil años de liberación.\r\nDejad que fluya su gran amor\r\ny que sane todas las heridas.\r\nDejad que os cubra con su gran paz\r\ny que os llene de gozo y perdón.\r\nPara celebrar dos mil a	ños de luz,\r\ndos mil años de miseri	cordia.\r\nPara celebrar dos mil años de luz,\r\ndos mil años de liberación.'),
(8, 'Estamos de fiesta', 'Estamos de fiesta con Jesús\r\nAl cielo queremos ir\r\nY todos reunidos en la mesa\r\nEs Cristo quien va a servir\r\nEstamos de fiesta con Jesús\r\nAl cielo queremos ir\r\nY todos reunidos en la mesa\r\nEs Cristo quien va a servir\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nÉl sana, el salva\r\nPoderoso es el señor\r\nBautiza, él viene\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nEl Padre, el hijo\r\nPoderoso es el señor\r\nEspíritu santo\r\nPoderoso es el señor\r\nEstamos de fiesta con Jesús\r\nAl cielo queremos ir\r\nY todos reunidos en la mesa\r\nEs Cristo quien va a servir\r\nEstamos de fiesta con Jesús\r\nAl cielo queremos ir\r\nY todos reunidos en la mesa\r\nEs Cristo quien va a servir\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nEl sana, él salva\r\nPoderoso es el señor\r\nBautiza, él viene\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nEl Padre, él hijo\r\nPoderoso es el señor\r\nEspíritu santo\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nEl sana, el salva\r\nPoderoso es el señor\r\nBautiza, él viene\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nPoderoso es el señor\r\nEl Padre, el hijo\r\nPoderoso es el señor\r\nEspíritu santo\r\nPoderoso es el señor'),
(9, 'Vamos con gozo', 'Vamos con gozo, el Padre nos llama\r\nVen en espíritu y en verdad\r\nVamos cantando, dándole gracias.  \r\nTodo ha hecho nuevo nuestro salvador.\r\n¡ALEGRATE, OH, PUEBLO SANTO!\r\nVEN Y ALABALE A ÉL\r\nY POSTRATE EN SU PRESENCIA:\r\n¡CRISTO DE REYES ES REY!\r\nDios creador misericordioso\r\nRodeado de gracia y digno de honor\r\nSentado en el trono reinando glorioso\r\nAhora y por siempre alabanzas a Él.\r\nAl son de trompetas vendrán las naciones.\r\nLos sacerdotes en su nombre irán\r\nMostrando el poder a las generaciones\r\ny con alabanzas su Reino de paz'),
(10, 'Pueblo de Dios', 'Pueblo de Dios, pueblo de Dios,\r\ncantad con alegría a nuestro Salvador.\r\nPueblo de Dios, pueblo de Dios,\r\ncantad con alegría a nuestro Salvador.\r\nCada mañana te buscaré\r\ny a cada instante te adoraré.\r\nCada mañana te buscaré\r\ny a cada instante te adoraré.\r\n ¡Oh, oh, oh, Señor!,\r\nte alabo con el alma y con el corazón.\r\n ¡Oh, oh, oh, Señor!,\r\nte alabo con el alma y con el corazón.'),
(11, 'Álabandote', '¿Cómo podré agradecer\r\ntanta bendición?\r\n¿Cómo podré responder a tu amor?\r\nLevantando mis manos, Señor,\r\ndeclarando que Tú eres Dios\r\ny dejándome llenar por el soplo\r\nde tu amor.\r\nY alabándote,\r\ny alabándote\r\ny alabándote,\r\nSeñor, mi Dios\r\nY alabándote,\r\ny alabándote\r\ny alabándote,\r\nSeñor, mi Dios\r\nQuiero adorarte Señor\r\ndesde el corazón\r\ny descalzarme ante ti, Santo Dios.\r\nTu perfume me inunda, Señor,\r\ntu presencia está dentro de mi\r\ny tu gracia se derrama por todo este lugar'),
(12, 'Acerquemonos todos al altar', 'ACERQUÉMONOS TODOS AL ALTAR\r\nQUE ES LA MESA FRATERNA DEL AMOR,\r\nPUES SIEMPRE QUE COMEMOS DE ESTE PAN\r\nRECORDAMOS LA PASCUA DEL SEÑOR.\r\nLos hebreos, en medio del desierto, comieron el maná,\r\nnosotros, peregrinos de la vida, comemos este pan;\r\nLos primeros cristianos ofrecieron su cuerpo como trigo;\r\nnosotros, acosados por la muerte, bebemos este vino.\r\nComo Cristo hecho pan de cada día, se ofrece en el alta|\r\nnosotros entregados al hermano comemos de este pan;\r\nComo el cuerpo de Cristo es uno solo por todos ofrecidos\r\nnosotros olvidando divisiones bebemos este vino.\r\nComo ciegos en busca de la aurora, dolientes tras la paz\r\nbuscando tierra nueva y cielos nuevos, comamos este pan.\r\nAcerquémonos todos los cansados, porque El es nuestro alivio\r\ny siempre que el desierto nos agobie, bebamos este vino.'),
(14, 'No Te Miedo', 'Cristo, tu cruz es respuesta real\r\nPara este mundo, para este tiempo\r\nQue huye en temores\r\nTú eres camino, eres verdad, eres la vida\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar\r\nAl verte herido reinando en la cruz\r\nDices mi nombre\r\nSuenan tambores al escucharte\r\nOigo las voces de aquellos hombres\r\nQue tienen hambre\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar\r\nSanta María, me acojo a tu amor\r\nPido tu fuego que arde de ruegos hoy por tus hijos\r\nVirgen María, Rosa del cielo, oye mi canto\r\nNo tengo miedo de la libertad\r\nNo tengo miedo, señor de la vida\r\nMe quiero entregar\r\nToma mis manos, mi voz y mi andar\r\nY yo alzaré alto la cruz derramada de amor\r\nPara que sea bandera de la juventud\r\nTu triunfo santo que junto a mi canto\r\nSe harán fuerte luz\r\nPara que vean tu rostro Jesús\r\nHombres con sed\r\nHombres valientes que quieran seguir tu caminar');

